import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { Task, Activity } from '../types';
import { storage } from '../utils/storage';

interface TaskContextType {
  tasks: Task[];
  activities: Activity[];
  addTask: (task: Omit<Task, 'id' | 'createdAt'>) => void;
  updateTask: (id: string, updates: Partial<Task>) => void;
  deleteTask: (id: string) => void;
  moveTask: (id: string, newColumn: 'todo' | 'doing' | 'done') => void;
  resetBoard: () => void;
  searchQuery: string;
  setSearchQuery: (query: string) => void;
  priorityFilter: 'all' | 'low' | 'medium' | 'high';
  setPriorityFilter: (filter: 'all' | 'low' | 'medium' | 'high') => void;
  sortBy: 'none' | 'dueDate';
  setSortBy: (sort: 'none' | 'dueDate') => void;
}

const TaskContext = createContext<TaskContextType | undefined>(undefined);

export const TaskProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [activities, setActivities] = useState<Activity[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [priorityFilter, setPriorityFilter] = useState<'all' | 'low' | 'medium' | 'high'>('all');
  const [sortBy, setSortBy] = useState<'none' | 'dueDate'>('none');

  useEffect(() => {
    setTasks(storage.getTasks());
    setActivities(storage.getActivities());
  }, []);

  useEffect(() => {
    storage.saveTasks(tasks);
  }, [tasks]);

  useEffect(() => {
    storage.saveActivities(activities);
  }, [activities]);

  const addActivity = (type: Activity['type'], taskId: string, taskTitle: string, details?: string) => {
    const newActivity: Activity = {
      id: Date.now().toString(),
      type,
      taskId,
      taskTitle,
      timestamp: new Date().toISOString(),
      details
    };
    setActivities(prev => [newActivity, ...prev].slice(0, 50)); // Keep only last 50 activities
  };

  const addTask = (taskData: Omit<Task, 'id' | 'createdAt'>) => {
    const newTask: Task = {
      ...taskData,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    setTasks(prev => [...prev, newTask]);
    addActivity('created', newTask.id, newTask.title);
  };

  const updateTask = (id: string, updates: Partial<Task>) => {
    setTasks(prev => prev.map(task => 
      task.id === id ? { ...task, ...updates } : task
    ));
    const task = tasks.find(t => t.id === id);
    if (task) {
      addActivity('edited', id, task.title);
    }
  };

  const deleteTask = (id: string) => {
    const task = tasks.find(t => t.id === id);
    setTasks(prev => prev.filter(task => task.id !== id));
    if (task) {
      addActivity('deleted', id, task.title);
    }
  };

  const moveTask = (id: string, newColumn: 'todo' | 'doing' | 'done') => {
    const task = tasks.find(t => t.id === id);
    setTasks(prev => prev.map(task => 
      task.id === id ? { ...task, column: newColumn } : task
    ));
    if (task && task.column !== newColumn) {
      addActivity('moved', id, task.title, `from ${task.column} to ${newColumn}`);
    }
  };

  const resetBoard = () => {
    setTasks([]);
    setActivities([]);
    storage.resetBoard();
  };

  const value: TaskContextType = {
    tasks,
    activities,
    addTask,
    updateTask,
    deleteTask,
    moveTask,
    resetBoard,
    searchQuery,
    setSearchQuery,
    priorityFilter,
    setPriorityFilter,
    sortBy,
    setSortBy
  };

  return <TaskContext.Provider value={value}>{children}</TaskContext.Provider>;
};

export const useTask = (): TaskContextType => {
  const context = useContext(TaskContext);
  if (context === undefined) {
    throw new Error('useTask must be used within a TaskProvider');
  }
  return context;
};
